function roundDays(start, end, offset) {
    var days = Floor(DateDiff(start, end, "days")) + offset;
    if (days <= 14) {
        return days + " days";
    }
    if (days <= 49) {
        return Ceil(days / 7) + " weeks";
    }
    if (days <= 365) {
        return Round(DateDiff(start, end, "months")) + " months";
    }
    return Round(DateDiff(start, end, "years"), 1) + " years";
}

